import{_ as t,a,o as r,X as o}from"./chunks/framework.Cp7KI1mb.js";const _=JSON.parse('{"title":"Wakatimeでプロジェクトの名前を指定する","description":"","frontmatter":{"layout":"post","title":"Wakatimeでプロジェクトの名前を指定する","category":"programming","tags":["TIL"],"head":[["link",{"rel":"canonical","href":"https://www.codingfeline.com/2016/02/15/customize-wakatime-project/"}],["meta",{"property":"og:url","content":"https://www.codingfeline.com/2016/02/15/customize-wakatime-project/"}],["meta",{"property":"og:title","content":"Wakatimeでプロジェクトの名前を指定する"}],["meta",{"property":"twitter:card","content":"summary_large_image"}],["meta",{"property":"twitter:site","content":"@_yshrsmz"}],["meta",{"property":"twitter:creator","content":"@_yshrsmz"}],["meta",{"property":"og:image","content":"https://www.codingfeline.com/2016/02/15/customize-wakatime-project/ogp.png"}]]},"headers":[],"relativePath":"2016/02/15/customize-wakatime-project/index.md","filePath":"posts/2016/2016-02-15-customize-wakatime-project.md","date":{"time":"2016-02-15","string":"February 15, 2016","year":"2016","month":"02","day":"15"}}'),i={name:"2016/02/15/customize-wakatime-project/index.md"};function p(m,e,n,c,s,l){return r(),a("div",null,[...e[0]||(e[0]=[o('<p><a href="https://wakatime.com" target="_blank" rel="noreferrer">Wakatime</a>はプログラミングしている時間を集計して、プロジェクト毎・言語毎にグラフ化してくれるツールです。</p><p>AtomやSublimeText, Jetbrains製の各種IDE、Eclipse, Vim等<a href="https://wakatime.com/editors" target="_blank" rel="noreferrer">様々なエディタのプラグイン</a>を提供しているので、言語毎にエディタを使い分けている方でも大体カバーできるんじゃないかと思います。</p><p>私の場合はAndroid Studio, IntelliJ IDEA, Atomで利用しています。</p><p>一週間毎に今週はどのプロジェクトでどのくらいコーディングしましたよー、っていうレポートを送ってくれるので週次の振り返りに重宝します。あとは大体の工数把握に使ったりとか。<br><a href="/2015/11/10/omnitweety-andrid-released-share-url-twitter/">Omnitweetyを作った時</a>も、wakatimeでコーディングした時間管理してました</p><p>集計時のプロジェクト名ですが、大体はフォルダ構造から自動で取得してくれるのですが、たまにうまく取得できず一つのプロジェクトが複数の名前で分けて集計されてしまったりします。</p><p>任意の名前でまとめられないのかなーって調べてたら、<a href="https://wakatime.com/help/faq/general#set-project-name" target="_blank" rel="noreferrer">公式のFAQにしっかりと記述がありました</a></p><p>Wakatimeでは３つの方法でプロジェクト名を指定できるそうです。</p><ol><li><p>git, subversion等のバージョン管理システムを利用する <code>git init</code> で初期化したフォルダの下が一つのプロジェクトとして認識されるようです。プロジェクト名はトップのフォルダ名です。</p></li><li><p>Wakatimeサイト上の<a href="https://wakatime.com/settings/preferences" target="_blank" rel="noreferrer">設定ページ</a> ファイルパスにこの文字列が含まれていたらこのプロジェクト、というような細かい条件でプロジェクト名を設定することができます。</p></li><li><p><code>.wakatime-project</code> ファイルを作成する。 プロジェクトディレクトリに <code>.wakatime-project</code> ファイルを作成し、一行目に利用したいプロジェクト名を書くと、その文字列がそのディレクトリ以下のプロジェクト名として利用されます。</p></li></ol><p>とりあえず <code>.wakatime-project</code> ファイルを作る方法が一番環境による影響をうけないので良さそうです。gitにコミットしておけば、複数の環境で開発している時も安心です。</p>',9)])])}const w=t(i,[["render",p]]);export{_ as __pageData,w as default};
